package myfirstpackage;

public class learningDataType {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int a = 2;                  //to declare variable and store regualr integer whole number 
		float b = 20;              //to declare variable with places of decimal
		double c = 123.444;
		String testing = "test";   //to declare testing  
		char data = 'd';          //to declare char data type turns string into character 
		
		
		System.out.println("This is my \n first data type int =" + a);              // \n for break line
		System.out.println("This is my \n second data type float =" + b);           // 
		System.out.println("This is my \n third data type double =" + c);
		System.out.println(testing);
		System.out.println(data);
		
		data++; 
		System.out.println(data);

	}

}
